import React, { useState, useEffect } from "react";
import styled from "@emotion/styled";

const API_ENDPOINT =
  "https://student-json-api.lidemy.me/comments?_sort=createdAt&_order=desc";

const Page = styled.div`
  width: 300px;
  margin: 0 auto;
`;

const Title = styled.h1`
  color: #ff6c94;
`;

const MessageForm = styled.form`
  margin-top: 16px;
`;

const MessageTextArea = styled.textarea`
  display: block;
  width: 100%;
`;
const SubmitButton = styled.button`
  margin-top: 8px;
`;
const MessageList = styled.div`
  margin-top: 16px;
`;

const MessageContainer = styled.div`
  border: 1px solid black;
  padding: 8px 16px;
  border-radius: 8px;

  & + & {
    margin-top: 8px;
  }
`;

const MessageHead = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const MessageAuther = styled.div`
  color: rgba(67, 44, 2);
  font-size: 14px;
`;

const MessageTime = styled.div``;
const MessageBody = styled.div`
  font-size: 16px;
  margin-top: 16px;
`;

const ErrorMessage = styled.div`
  margin-top: 16px;
  color: red;
`;

function Message({ author, time, children }) {
  return (
    <MessageContainer>
      <MessageHead>
        <MessageAuther>{author}</MessageAuther>
        <MessageTime>{time}</MessageTime>
      </MessageHead>
      <MessageBody>{children}</MessageBody>
    </MessageContainer>
  );
}

function App() {
  const [messages, setMessages] = useState(null);
  const [apiError, setApiError] = useState(null);

  //做一些事=>eventlistener,useEffect
  //component render完拿資料＝>useEffect

  useEffect(() => {
    fetch(API_ENDPOINT)
      .then((res) => res.json())
      .then((data) => {
        setMessages(data);
      })
      .catch((err) => {
        setApiError(err.message);
      });
  }, []);

  return (
    <Page>
      <Title>留言板</Title>
      <MessageForm>
        <MessageTextArea rows={10} />
        <SubmitButton>送出留言</SubmitButton>
      </MessageForm>
      {apiError && (
        //render object會有什麼問題？ 防呆機制
        <ErrorMessage>Sometheing went wrong.{apiError.toString()}</ErrorMessage>
      )}
      {messages && messages.length === 0 && <div>No Message</div>}
      <MessageList>
        {messages &&
          messages.map((message) => (
            <Message
              key={message.id}
              author={message.nickname}
              time={message.createdAt}
            >
              {message.body}
            </Message>
          ))}
      </MessageList>
    </Page>
  );
}

export default App;
