import React, { useState, useEffect } from "react";
import styled from "@emotion/styled";

const API_ENDPOINT =
  "https://student-json-api.lidemy.me/comments?_sort=createdAt&_order=desc";

const Page = styled.div`
  width: 300px;
  margin: 0 auto;
`;

const Title = styled.h1`
  color: #ff6c94;
`;

const MessageForm = styled.form`
  margin-top: 16px;
`;

const MessageTextArea = styled.textarea`
  display: block;
  width: 100%;
`;
const SubmitButton = styled.button`
  margin-top: 8px;
`;
const MessageList = styled.div`
  margin-top: 16px;
`;

const MessageContainer = styled.div`
  border: 1px solid black;
  padding: 8px 16px;
  border-radius: 8px;
`;

const MessageHead = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const MessageAuther = styled.div`
  color: rgba(67, 44, 2);
  font-size: 14px;
`;

const MessageTime = styled.div``;
const MessageBody = styled.div`
  font-size: 16px;
  margin-top: 16px;
`;

function Message({ author, time, children }) {
  return (
    <MessageContainer>
      <MessageHead>
        <MessageAuther>{author}</MessageAuther>
        <MessageTime>{time}</MessageTime>
      </MessageHead>
      <MessageBody>{children}</MessageBody>
    </MessageContainer>
  );
}

function App() {
  const [message, setMessage] = useStat([]);

  //做一些事=>eventlistener,useEffect
  //component render完拿資料＝>useEffect

  useEffect(() => {
    fetch("");
  }, []);

  return (
    <Page>
      <Title>留言板</Title>
      <MessageForm>
        <MessageTextArea rows={10} />
        <SubmitButton>送出留言</SubmitButton>
      </MessageForm>
      <MessageList>
        <Message author={"su"} time="2022-03-18">
          My Message
        </Message>
      </MessageList>
    </Page>
  );
}

export default App;
